// src/BusinessCard.js

import React from 'react';
import styled, { keyframes } from 'styled-components';

const glow = keyframes`
  0% {
    box-shadow: 0 0 5px #ff0000, 0 0 10px #ff0000, 0 0 20px #ff0000, 0 0 40px #ff0000;
  }
  100% {
    box-shadow: 0 0 10px #ff0000, 0 0 20px #ff0000, 0 0 40px #ff0000, 0 0 80px #ff0000;
  }
`;

const Card = styled.div`
  background: #000;
  color: #fff;
  padding: 20px;
  border-radius: 10px;
  width: 300px;
  text-align: center;
  font-family: 'Arial', sans-serif;
  animation: ${glow} 1.5s infinite alternate;
`;

const Name = styled.h1`
  color: #ff0000;
  font-size: 24px;
  margin-bottom: 10px;
`;

const Alias = styled.h2`
  color: #ff0000;
  font-size: 20px;
  margin-bottom: 20px;
`;

const Skills = styled.p`
  font-size: 14px;
  margin-bottom: 20px;
`;

const Links = styled.div`
  display: flex;
  justify-content: space-around;
`;

const Link = styled.a`
  color: #fff;
  text-decoration: none;
  font-size: 16px;
  &:hover {
    color: #ff0000;
  }
`;

const BusinessCard = () => {
  return (
    <Card>
      <Name>Cr1ppl3</Name>
      <Alias>Developer from Sydney, Australia</Alias>
      <Skills>
        C++, C#, Python, PHP, HTML, JS, Java
      </Skills>
      <Links>
        <Link href="https://github.com/Cr1ppl3" target="_blank">Github</Link>
        <Link href="https://discord.com" target="_blank">Discord</Link>
        <Link href="https://youtube.com" target="_blank">YouTube</Link>
      </Links>
    </Card>
  );
};

export default BusinessCard;
