// src/BusinessCard.js

import React, { useEffect, useState } from 'react';
import './BusinessCard.css';
import { FaGithub, FaDiscord, FaYoutube } from 'react-icons/fa';
import { GiAustralia, GiSouthAfrica, GiGamepad } from 'react-icons/gi';

const BusinessCard = () => {
  const [header, setHeader] = useState('');
  const [subHeader, setSubHeader] = useState('');
  const headerText = 'Cr1ppl3';
  const subHeaderText = 'Developer from Sydney, Australia';

  useEffect(() => {
    let headerIndex = 0;
    let subHeaderIndex = 0;

    const typeHeader = () => {
      if (headerIndex < headerText.length) {
        setHeader((prev) => prev + headerText.charAt(headerIndex));
        headerIndex++;
        setTimeout(typeHeader, 100);
      } else {
        typeSubHeader();
      }
    };

    const typeSubHeader = () => {
      if (subHeaderIndex < subHeaderText.length) {
        setSubHeader((prev) => prev + subHeaderText.charAt(subHeaderIndex));
        subHeaderIndex++;
        setTimeout(typeSubHeader, 100);
      }
    };

    typeHeader();
  }, []);

  return (
    <div className="card">
      <h1 className="name">{header}</h1>
      <h2 className="alias">{subHeader}</h2>
      <div className="details">
        <p>
          I'm the developer of a game-enhancing software, and I do freelance work.
        </p>
        <p className="flag">
          <GiAustralia /> Sydney, Australia
        </p>
        <p className="flag">
          <GiSouthAfrica /> Born in South Africa
        </p>
        <p className="experience">
          10 years of experience | Age: 27
        </p>
      </div>
      <p className="skills">
        Skills: C++, C#, Python, PHP, HTML, JS, Java
      </p>
      <div className="links">
        <a href="https://github.com/Cr1ppl3" target="_blank" rel="noopener noreferrer">
          <FaGithub /> GitHub
        </a>
        <a href="https://discord.com" target="_blank" rel="noopener noreferrer">
          <FaDiscord /> Discord
        </a>
        <a href="https://youtube.com" target="_blank" rel="noopener noreferrer">
          <FaYoutube /> YouTube
        </a>
      </div>
      <div className="software">
        <GiGamepad /> Game-Enhancing Software
      </div>
    </div>
  );
};

export default BusinessCard;
